class Llvm38asan < Formula
  desc "llvm 3.8 with asan"
  homepage "http://llvm.org/"
  url "https://people.mozilla.org/~eakhgari/llvm38asan-3.8.0.bottle.tar.gz"
  version "3.8"
  sha256 "e42d4a93d32ca982a9118636b430f7ab020b3ab7ecbb4faa431fefcfec13c62e"

  def install
    bin.install "bugpoint"
    bin.install "c-index-test"
    bin.install "clang"
    bin.install "clang-apply-replacements"
    bin.install "clang-check"
    bin.install "clang-format"
    bin.install "clang-query"
    bin.install "clang-rename"
    bin.install "clang-tblgen"
    bin.install "clang-tidy"
    bin.install "count"
    bin.install "FileCheck"
    bin.install "llc"
    bin.install "lli"
    bin.install "lli-child-target"
    bin.install "llvm-ar"
    bin.install "llvm-as"
    bin.install "llvm-bcanalyzer"
    bin.install "llvm-config"
    bin.install "llvm-cov"
    bin.install "llvm-cxxdump"
    bin.install "llvm-diff"
    bin.install "llvm-dis"
    bin.install "llvm-dsymutil"
    bin.install "llvm-dwarfdump"
    bin.install "llvm-dwp"
    bin.install "llvm-extract"
    bin.install "llvm-link"
    bin.install "llvm-lto"
    bin.install "llvm-mc"
    bin.install "llvm-mcmarkup"
    bin.install "llvm-nm"
    bin.install "llvm-objdump"
    bin.install "llvm-pdbdump"
    bin.install "llvm-profdata"
    bin.install "llvm-readobj"
    bin.install "llvm-rtdyld"
    bin.install "llvm-size"
    bin.install "llvm-split"
    bin.install "llvm-stress"
    bin.install "llvm-symbolizer"
    bin.install "llvm-tblgen"
    bin.install "not"
    bin.install "obj2yaml"
    bin.install "opt"
    bin.install "pp-trace"
    bin.install "sancov"
    bin.install "verify-uselistorder"
    bin.install "yaml2obj"
    prefix.install Dir["lib/*"]
    prefix.install Dir["share/*"]
  end
end
